mapper.select:
SELECT a.id, a.company_name companyName, a.company_desc companyDesc, a.p_id pId, b.`name` provinceName, c.`name` cityName, d.`name` areaName, e.`name` townshipName, a.address_detailed addressDetailed, (SELECT COUNT(*) FROM company_mation b WHERE a.id = b.p_id) childsNum, CONVERT(a.create_time, char) createTime FROM company_mation a LEFT JOIN t_area b ON a.address_province = b.id LEFT JOIN t_area c ON a.address_city = c.id LEFT JOIN t_area d ON a.address_area = d.id LEFT JOIN t_area e ON a.address_township = e.id WHERE 1=1
AND a.company_name LIKE '%${companyName}%'
AND a.address_province = #{provinceId}
AND a.address_city = #{cityId}
AND a.address_area = #{areaId}
AND a.address_township = #{townshipId}
ORDER BY a.create_time DESC
